mapper.select:
select
  product_id, user_id, activate_time, org_id, isover_time, activate_image_url, create_time, update_time
from wistron.product_maintain


        and product_id = #{productId}


        and user_id = #{userId}


        and activate_time = #{activateTime}


        and org_id = #{orgId}


        and isover_time = #{isoverTime}


        and activate_image_url = #{activateImageUrl}


        and create_time = #{createTime}


        and update_time = #{updateTime}
